if ( $.trim($(this).val()) === $(this).data(&amp;quot;txt&amp;quot;) ) &amp;#123;
$(this).val(&amp;quot;&amp;quot;);
&amp;#125;
&amp;#125;).blur(function()&amp;#123;
// 为有class clear的域添加blur时间来恢复默认值
// 但如果class是once则忽略
if ( $.trim($(this).val()) === &amp;quot;&amp;quot; &amp;amp;&amp;amp; !$(this).hasClass(&amp;quot;once&amp;quot;) ) &amp;#123;
//Restore saved data
$(this).val( $(this).data(&amp;quot;txt&amp;quot;) );
&amp;#125;
&amp;#125;);
&amp;#125;);
&lt;/code&gt;&lt;/pre&gt;&lt;p&gt;&lt;a href=&#34;http://www.tripwiremagazine.com/wp-content/uploads/images/stories/Articles/jquery-tips/demo.html&#34;&gt;查看Demo&lt;/a&gt;&lt;/p&gt;
